import React, { useState } from "react";
import { Card, TableContainer, Table, TableHead, TableBody, TableRow, TableCell, Input} from "@mui/material";
import CardContent from "@mui/material/CardContent";
import { Button } from "@mui/material";

const Comanda = ({ content, cuenta }) => {

  return (
    <div>
      <Card id="Comanda">
        <CardContent>
      <TableContainer>
        <Table>
          <TableHead><Input variant='container' placeHolder='Cliente'></Input>
            <TableRow>
              <TableCell>Product</TableCell>
              <TableCell>Precio</TableCell>
            </TableRow>
          </TableHead>
        </Table>
      </TableContainer>
          {content.map((item, index) => (
            <p key={index} value={item.price} margin="normal">
              {item.product} {"$" + item.precio}
            </p>
          ))}
          <h2>Total:{cuenta}</h2>

        </CardContent>
      </Card>
    </div>
  );
};

export default Comanda;
