import { useState, useEffect } from "react";
import Axios from "axios";

export const useProductsList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
      Axios({
        url: "http://localhost:4000/products",
      })
        .then((response) => {
          setProducts(response.data);

        })
        .catch((error) => {
          console.log(error);
        });
    }, [setProducts]);
return products

}
