import React, {useEffect, useState}  from "react";
import Comanda from './Comanda'
import { useProductsList } from "../hooks/useProductsList";
function ButtonsDesayuno() {
  const regresar= ()=>{
    window.location.href='/menu'
  }
   const {desayuno}= useProductsList()
   const [localOrder, setLocalOrder] = useState({});
 const imprimir = (e, value, name)=>{
   e.preventDefault()

   setLocalOrder({...localOrder, precio: value, product:name})
   console.log(localOrder)
 }


    return (
    <div className='contenedorBotones'><Comanda  />
        <section id="desayunoButttons">
          {desayuno.map((item) => (
            <div key={item.id}>
              <Button value={item.price} name={item.name }variant="contained" margin="normal" color="secondary" onClick={(e) => imprimir(e, e.target.value, e.target.name)}>
        {item.name} {'$'+ item.price}
      </Button>

            </div>
          ))}
        </section>
        <Button  onClick={regresar}id='regresarMenu' variant="contained" margin="large" color="secondary">
          Regresar
        </Button>
     </div>
    );
  }




export default ButtonsDesayuno;
