import Button from "@mui/material/Button";
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { iconoPassword } from "./images/iconoPassword";
import { iconoNotPassword } from "./images/iconoNotPassword";
import { emailIcon } from "./images/emailIcon";
import { signInWithEmailAndPassword, auth } from "../lib/firebaseConfig";

function Form() {
  const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [visible, setVisible] = useState(false);
    const toggleVisibility = () => {
      setVisible(!visible);
    };

 const enviarDatos = (event, email, password) => {
    event.preventDefault();
    console.log(email, password);
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        console.log(user.uid);
        window.location.href = "/menu";

        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
      });
  };
     return (
      <form onSubmit={(e) => enviarDatos(e, email, password)} className='form' id='form' data-testid="form">
      <h1>Inicia Sesi칩n</h1>
      <TextField
        fullWidth
        autoFocus
        color="secondary"
        margin="normal"
        variant="outlined"
        className="email"
        type="email"
        label="Correo electr칩nico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
             {emailIcon}
            </InputAdornment>
          ),
        }}
      />
      <TextField
        fullWidth
        color="secondary"
        margin="normal"
        variant="outlined"
        className="password"
        type={visible ? "text" : "password"}
        label="Contrase침a"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        InputProps={{
          endAdornment: (
            <InputAdornment position="end" onClick={toggleVisibility}>
              {visible ? iconoPassword : iconoNotPassword}
            </InputAdornment>
          ),
        }}
      />

      <Button
        fullWidth
        id="Login"
        variant="contained"
        margin="large"
        type="submit"
        data-testid='Login'


      >
        Iniciar Sesi칩n
      </Button>
    </form> 

  )
}

export default Form
