import { Button } from "@mui/material";
import React, { Fragment } from "react";
import validate from '../axios'
import ReactDOM from 'react-dom';
import Menu from './Menu'



function MenusCointeiner() {
  const showDesayunoItems = () => {

      // the data
      const myData = validate('http://localhost:4000/products')
      // the loop
      const children = myData.map((val) => (
          React.createElement("p", {id: val["key"]}, val["name"])
      ));
      // the div with children inside
      return (
        React.createElement("div", {className: "contexCon"},
          children
        ),
      ReactDOM.render(React.createElement(Menu, {}), document.getElementById("menusContainer")))}








  const showComidaItems = () => {
    window.location.href = "/menu/comida";
  };
  const regresar = () => {
    window.location.href = "/";
  };
     return ( 


        <div>
     <section id='menusContainer' className="container"> <Button
        color="secondary"
        id="Desayuno"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
        onClick={showDesayunoItems}
      >
        Desayuno
      </Button>
      <Button
        color="secondary"
        id="AlmuerzoCena"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
        onClick={showComidaItems}
      >
        Almuerzo y Cena
      </Button>
     </section>
     <Button
          onClick={regresar}
          id="regresarMenu"
          variant="contained"
          margin="large"
          color="secondary"
        >
          Salir
        </Button>


      </div>
    ) 



}

export default MenusCointeiner;
