import { Button } from "@mui/material";
import React from "react";
import { useProductsList } from "../hooks/useProductsList";
import useLocalOrder from "../hooks/useLocalOrder";

function ButtonsComida() {
  const regresar = () => {
    window.location.href = "/menu";
  };

  const { comida }= useProductsList()
  const {localOrder, order} = useLocalOrder();


  return (
    <div className="contenedorBotones">
       <section id='Comanda'>
     {order.map((item) => (
          <div key={item.id}>
            <p
              value={item.price}
              name={item.name}

              margin="normal"


            >
              {item.product} {"$" + item.precio}
            </p>
          </div>
        ))}
     </section>
      <section id="desayunoButttons">
        {comida.map((item) => (
          <div key={item.id}>
            <Button variant="contained" margin="large" color="secondary" onClick={() => 
                localOrder(item.price, item.name)
              } >
              {item.name} {"$" + item.price}
            </Button>
          </div>
        ))}
      </section>
      <Button
        onClick={regresar}
        id="regresarMenu2"
        variant="contained"
        margin="large"
        color="secondary"
      >
        Regresar
      </Button>
    </div>
  );
}

export default ButtonsComida;
