import { Button } from "@mui/material";
import React, {useState, Fragment} from "react";
import { useProductsList } from "../hooks/useProductsList";
import useLocalOrder from "../hooks/useLocalOrder";
import Comanda from "./Comanda";
import ButtonBack from "./ButtonBack";
function ButtonsDesayuno() {
  const regresar = () => {
    window.location.href = "/menu";
  };
  const { desayuno } = useProductsList();

  const { localOrder, order } = useLocalOrder();
  const [suma, setSuma]= useState(0)
  const sumar= (precio)=>{
    setSuma(suma+precio)
    console.log(suma)
  }

  return (
    <Fragment >
      <Comanda content={order} />
      <section id="desayunoButttons">
        {desayuno.map((item) => (
          <Button
            key={item.id}
            value={item.price}
            name={item.name}
            variant="contained"
            margin="normal"
            color="secondary"
            onClick={() => {localOrder(item.price, item.name); sumar(item.price)}}
          >
            {item.name} {"$" + item.price}
          </Button>
        ))}
      </section>
      <ButtonBack />
    </Fragment>
  );
}

export default ButtonsDesayuno;
