import React, { useState } from "react";
import { Card, TableContainer, Table, TableHead, TableFooter, TableBody, TableRow, TableCell, TextField} from "@mui/material";
import CardContent from "@mui/material/CardContent";
import { Button } from "@mui/material";
import TablePagination from '@mui/material/TablePagination';
import Paper from '@mui/material/Paper';
import IconButton from '@mui/material/IconButton';
import FirstPageIcon from '@mui/icons-material/FirstPage';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import LastPageIcon from '@mui/icons-material/LastPage';

const Comanda = ({ content, cuenta }) => {
  const theme = useTheme();
  const { count, page, rowsPerPage, onPageChange } = props;

  const handleFirstPageButtonClick = (event) => {
    onPageChange(event, 0);
  };
  const handleBackButtonClick = (event) => {
    onPageChange(event, page - 1);
  };

  const handleNextButtonClick = (event) => {
    onPageChange(event, page + 1);
  };

  const handleLastPageButtonClick = (event) => {
    onPageChange(event, Math.max(0, Math.ceil(count / rowsPerPage) - 1));
  };
  return (
    <div>
      <Card id="Comanda">
        <section id='cliente'><h2>Cliente:</h2><TextField size='small' variant="filled"></TextField></section>

       <CardContent>
      <TableContainer>
        <Table stickyHeader aria-label="sticky table">
        <TablePagination rowsPerPageOptions={[10, 50]} />
          <TableHead>
            <TableRow>
              <TableCell align='left'>Producto</TableCell>
              <TableCell align='center'>Precio</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
          {content.map((item, index) => (
            <TableRow >
              <TableCell  align='left' key={index} value={item.product} margin="normal">{item.product}</TableCell>
              <TableCell  align='center' key={item.id} value={item.price}>{"$" + item.precio}</TableCell>
            </TableRow>


          ))}
              </TableBody>
        </Table>
       </TableContainer>  


        </CardContent><section  id='Cuenta'>
              <h2  align="right" margin="normal">Total: </h2>
              <h2  align="right" margin="normal">$ {cuenta}</h2>
            </section>
      </Card>
    </div>
  );
};

export default Comanda;
