import Button from "@mui/material/Button";
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { enviarDatos } from "../lib/FirebaseAut"

function Form() {
  const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [visible, setVisible] = useState(false);
    const toggleVisibility = () => {
      setVisible(!visible);
    };
    const emailIcon = <img src='./images/emailIcon.svg'/>
    const iconoPassword = <img src='./images/iconoPassword.svg'/>
    const iconoNotPassword = <img src='./images/iconoNotPassword.svg'/>

      return (
      <form onSubmit={(e) => enviarDatos(e, email, password)} className='form' id='form' data-testid="form">
      <h1>Inicia Sesi칩n</h1>
      <TextField
        fullWidth
        autoFocus
        color="secondary"
        margin="normal"
        variant="outlined"
        id="email"
        type="email"
        label="Correo electr칩nico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        InputProps={{
          endAdornment: (
            <InputAdornment position="end" id='show'>
             {emailIcon}
            </InputAdornment>
          ),
        }}
      />
      <TextField
        fullWidth
        color="secondary"
        margin="normal"
        variant="outlined"
        id="password"
        type={visible ? "text" : "password"}
        label="Contrase침a"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
        InputProps={{
          endAdornment: (
            <InputAdornment position="end" onClick={toggleVisibility} id='unShow'>
              {visible ? iconoPassword : iconoNotPassword}
            </InputAdornment>
          ),
        }}
      />

      <Button
        fullWidth
        id="Login"
        variant="contained"
        margin="large"
        type="submit"
        data-testid='Login'


      >
        Iniciar Sesi칩n
      </Button>
    </form> 

  )
}

export default Form
