import axiosValidate from '../axios'
import ReactDOM from 'react-dom';


function MenusCointeiner() {
  const showDesayunoItems = () => {
    window.location.href = "/menu/desayuno";
  };
  const showComidaItems = () => {
    window.location.href = "/menu/comida";
  };
  const regresar = () => {
    window.location.href = "/";
  };
     return ( 


        <div>
     <section className="container"> <Button
        color="secondary"
        id="Desayuno"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
       /*  onClick={ReactDOM.render(
          <div>{axiosValidate('http://localhost:4000/products')}</div>,
          document.getElementById('root')
        )} */
      >
        Desayuno
      </Button>
      <Button
        color="secondary"
        id="AlmuerzoCena"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
        onClick={showComidaItems}
      >
        Almuerzo y Cena
      </Button>
     </section>
     <Button
          onClick={regresar}
          id="regresarMenu"
          variant="contained"
          margin="large"
          color="secondary"
        >
          Salir
        </Button>


      </div>
    ) 



}

export default MenusCointeiner;
