import { Button } from "@mui/material";
import React, { useState } from "react";
import { useProductsList } from "../hooks/useProductsList";
import useLocalOrder from "../hooks/useLocalOrder";
import Comanda from "./Comanda";
import ButtonBack from "./ButtonBack";

function ButtonsComida() {
  const { comida } = useProductsList();
  const { localOrder, order } = useLocalOrder();
  const [suma, setSuma]= useState(0)
  const sumar= (precio)=>{
    setSuma(suma+precio)
    console.log(suma)
  }
  return (
    <div className="contenedorBotones">
      <Comanda content={order} />
      <section id="desayunoButttons">
        {comida.map((item) => (
          <Button
            key={item.id}
            variant="contained"
            margin="large"
            color="secondary"
            onClick={() => localOrder(item.price, item.name), sumar(item.price)}
          >
            {item.name} {"$" + item.price}
          </Button>
        ))}
      </section>
      <ButtonBack />
    </div>
  );
}

export default ButtonsComida;
