import cocinero from "./images/cocinero.png";
import guardaespaldas from "./images/guardaespaldas.png";
import camarera from "./images/camarera.png";
import { Fragment } from "react";
import Button from "@mui/material/Button";
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { signInWithEmailAndPassword, auth } from "../lib/firebaseConfig";
import { iconoPassword } from "./images/iconoPassword";
import { iconoNotPassword } from "./images/iconoNotPassword";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");



  const enviarDatos = (event) => {
    event.preventDefault();
    console.log(email, password);
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        console.log(user.uid);
        window.location.href = "/menu";

        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
      });
  };

  return (
    <Fragment>
      <main>
        <div className="Usuarios">
          <img src={guardaespaldas} alt=""></img>
          <img src={camarera} alt=""></img>
          <img src={cocinero} alt=""></img>
        </div>
        <form onSubmit={enviarDatos}>
          <h1>Inicia Sesión</h1>
          <TextField
            fullWidth
            autoFocus
            color="secondary"
            margin="normal"
            variant="outlined"
            name="email"
            type="email"
            label="Correo electrónico"
            onChange={(e) => setEmail(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <svg
                    width="25"
                    height="24"
                    viewBox="0 0 25 24"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M22.8701 6C22.8701 4.9 21.9701 4 20.8701 4H4.87012C3.77012 4 2.87012 4.9 2.87012 6V18C2.87012 19.1 3.77012 20 4.87012 20H20.8701C21.9701 20 22.8701 19.1 22.8701 18V6ZM20.8701 6L12.8701 11L4.87012 6H20.8701ZM20.8701 18H4.87012V8L12.8701 13L20.8701 8V18Z"
                      fill="#A3A3A3"
                    />
                  </svg>
                </InputAdornment>
              ),
            }}
          />
          <TextField
            fullWidth
            color="secondary"
            margin="normal"
            variant="outlined"
            name="password"
            type={visible ? "text" : "password"}
            label="Contraseña"
            onChange={(e) => setPassword(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  {visible ? iconoPassword : iconoNotPassword}
                </InputAdornment>
              ),
            }}
          />

          <Button
            fullWidth
            id="Login"
            variant="contained"
            margin="large"
            type="submit"
          >
            Iniciar Sesión
          </Button>
        </form>
      </main>
    </Fragment>
  );
}
export default Login;
