import React, { Fragment } from "react";
import axiosValidate from '../axios'
import ReactDOM from 'react-dom';
import Menu from './Menu'
import axios from 'axios'


function MenusCointeiner() {
  const showDesayunoItems = () => {

      const buttons = axios.get('http://localhost:4000/products').then((response) => {
         return response.data.map((item) => {
           return (
             React.createElement("p", [item.name])
           );
         });
       });


    return (
      React.createElement("div", {className: "contexCon"},
       buttons
      ),
       ReactDOM.render(React.createElement(MenusCointeiner, {}), document.getElementById("root")));
      }

      // render




