import { Button } from "@mui/material";
import React, { Fragment } from "react";
import axiosValidate from '../axios'
import ReactDOM from 'react-dom';
import Menu from './Menu'
import axios from 'axios'


function MenusCointeiner() {
  const showDesayunoItems = () => {
    const myData = [
      {key: 1, name: "Hello"},
      {key: 2, name: "World"},
      {key: 3, name: "etc"}
    ];
    const children = myData.map((val) => (
      React.createElement("p", {id: val["key"]}, val["name"])
  ));



    return (
      ReactDOM.render(React.createElement("div", {className: "contexCon"},
      children) 
      , document.getElementById("menusContainer")))

      }

      // render




  const showComidaItems = () => {
    window.location.href = "/menu/comida";
  };
  const regresar = () => {
    window.location.href = "/";
  };
     return ( 


        <div>
     <section id='menusContainer' className="container"> <Button
        color="secondary"
        id="Desayuno"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
        onClick={showDesayunoItems}
      >
        Desayuno
      </Button>
      <Button
        color="secondary"
        id="AlmuerzoCena"
        variant="contained"
        margin="large"
        type="submit"
        data-testid="Desayuno"
        onClick={showComidaItems}
      >
        Almuerzo y Cena
      </Button>
     </section>
     <Button
          onClick={regresar}
          id="regresarMenu"
          variant="contained"
          margin="large"
          color="secondary"
        >
          Salir
        </Button>


      </div>
    ) 



}

export default MenusCointeiner;
