import React, { useState } from "react";
import { Card, TableContainer, Table, TableHead, TableBody, TableRow, TableCell, TextField} from "@mui/material";
import CardContent from "@mui/material/CardContent";
import { Button } from "@mui/material";

const Comanda = ({ content, cuenta }) => {

  return (
    <div>
      <Card id="Comanda">
        <section id='cliente'><h2>Cliente:</h2><TextField size='small' variant="fullfilled"></TextField></section>

       <CardContent>
      <TableContainer>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Producto</TableCell>
              <TableCell>Precio</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
          {content.map((item, index) => (
            <TableRow>
              <TableCell key={index} value={item.product} margin="normal">{item.product}</TableCell>
              <TableCell Key={item.id} value={item.price}>{"$" + item.precio}</TableCell>
            </TableRow>


          ))}
          </TableBody>
        </Table>
      </TableContainer>

          <h2>Total:{cuenta}</h2>

        </CardContent>
      </Card>
    </div>
  );
};

export default Comanda;
