import { Fragment } from "react";
import Button from "@mui/material/Button";
import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { iconoPassword } from "./images/iconoPassword";
import { iconoNotPassword } from "./images/iconoNotPassword";
import { emailIcon } from "./images/emailIcon";
import { enviarDatos } from "../lib/firebaseAuth";


const Form = onSubmit => {
  onSubmit = enviarDatos
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [visible, setVisible] = useState(false);
    const toggleVisibility = () => {
      setVisible(!visible);
    };
    return (

           <form onSubmit={onSubmit(email, password)} className='form' id='form' data-testid="form">
          <h1>Inicia Sesi칩n</h1>
          <TextField
            fullWidth
            autoFocus
            color="secondary"
            margin="normal"
            variant="outlined"
            name="email"
            type="email"
            label="Correo electr칩nico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                 {emailIcon}
                </InputAdornment>
              ),
            }}
          />
          <TextField
            fullWidth
            color="secondary"
            margin="normal"
            variant="outlined"
            name="password"
            type={visible ? "text" : "password"}
            label="Contrase침a"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            InputProps={{
              endAdornment: (
                <InputAdornment position="end" onClick={toggleVisibility}>
                  {visible ? iconoPassword : iconoNotPassword}
                </InputAdornment>
              ),
            }}
          />

          <Button
            fullWidth
            id="Login"
            variant="contained"
            margin="large"
            type="submit"
          >
            Iniciar Sesi칩n
          </Button>
        </form> 

    )
}



export default Form
