import Axios from "axios";
import React, { useState, useEffect } from "react";
const [products, setProducts] = useState([]);
  useEffect(() => {
    Axios({
      url: "http://localhost:4000/products",
    })
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [setProducts]);

  export default products
